local DlgTemplate = DlgTemplatelocal DlgApi  = DlgApilocal GameApi = GameApiWin_BattleScore5 = DlgTemplate:new({this = "Win_BattleScore5"})-------------------------------------界面初始化-----------------------------------function Win_BattleScore5:Init()	DlgApi.ShowItem(self.this, "Img_ShipScore", false)	DlgApi.ShowItem(self.this, "Gfx_Highlight", false)end-------------------------------------界面开启-----------------------------------function Win_BattleScore5:ShowDialog()	DlgApi.ShowItem(self.this, "Img_ShipScore", false)	DlgApi.ShowItem(self.this, "Gfx_Highlight", false)endfunction Win_BattleScore5:Tick()	if DlgApi.IsDialogShow("Win_BattleScore5") then		DlgApi.ShowItem("Win_CharHead", "Img_ShipScore", false)	endend