function Win_Cyclopaedia:Init(deltaTime)
	self:RegisterEvent("Edt_Search", self.OnSearch)
	self:RegisterEvent("Btn_Search", self.OnSearch)
	self:RegisterEvent("Btn_OnClean", self.OnClean)

	self:RegisterEvent("Rdo_1", self.OnSwitch1)
	self:RegisterEvent("Rdo_3", self.OnSwitch3)
	self:RegisterEvent("Rdo_4", self.OnSwitch4)

	self:RegisterEvent(WM_LBUTTONDOWN, self.OnLButtonDown);
--		self:RegisterEvent(WM_LBUTTONUP, self.OnLButtonUp)
	for i = 1, 45 do
	self:RegisterEvent(Format("%s%d", "Btn_Type", i), self.OnType)
	end

	for j = 1, 9 do
	self:RegisterEvent(Format("%s%d", "Btn_ClassId", j), self.OnClassId)
	end

	DlgApi.ShowItem(self.this, "Gfx_Searching", false)

	self.RdoGroupId = 1
end

--------------------------------------------
--判断输入框内是否输入文字
--------------------------------------------
function Win_Cyclopaedia:Tick(deltaTime)
   local times = deltaTime + 1000

   	if DlgApi.IsDialogShow(self.this) then
	   if times  > 1000 then
		   if DlgApi.GetItemText(self.this, "Edt_Search") == "" then
					DlgApi.EnableItem(self.this, "Btn_Search", false)
		   else
			  DlgApi.EnableItem(self.this, "Btn_Search", true)
		   end
		   times = times - 1000
	   end
	end
end

