function Win_Cyclopaedia:OnSearch()
alltitle = {}
GetSearchTxt = DlgApi.GetItemText(self.this, "Edt_Search")
IdSearchList = {}
local id = 0
for s = 1, 45 do
    DlgApi.EnableItem(self.this, Format("%s%d", "Btn_Type", s), false)
end
	if GetSearchTxt ~= "" then
		for k1, v1 in ipairs(ChiBiDataLib) do  -- 搜索显示关键字条目
			if nil ~= string.find(v1.Title, GetSearchTxt) or string.find(v1.Txt, GetSearchTxt) then
			   id = id + 1
			   IdSearchList[id] = v1.Classid
			   alltitle[id] = v1.Title
			   alltxt[id] = v1.Txt
			   	if next(IdSearchList) ~= nil then
					for k7, v7 in ipairs(IdSearchList) do  --判断分类按钮是否可用
						DlgApi.EnableItem(self.this, Format("%s%d", "Btn_Type", v7), true)
					end
				end
			else
			   IdSearchList = TabNull
			end
		end
		DlgApi.SetListText(self.this, "List_Catalog", alltitle)
	end
end

