				if GetTypeList[t] == v2.Classid then
				   num = num + 1
				   alltitle[num] = v2.Title
				   alltxt[num] = v2.Txt
				   break
				end
			end
		end
	DlgApi.SetListText(self.this, "List_Catalog", alltitle)
	else
		for k5, v5 in ipairs(ChiBiDataLib) do
			for k6, v6 in ipairs(IdSearchList) do
				for t = 1, 1 do
					if GetTypeList[t] == v6 then
					    num = num + 1
						List1[num] = v6
						for k8, v8 in ipairs(List1) do
							if v8 == v5.Classid then
							   num = num + 1
							   alltitle[num] = v5.Title
							   alltxt[num] = v5.Txt
							break
							end
					    end
					end
				end
			end
		end
		DlgApi.SetListText(self.this, "List_Catalog", alltitle)
	end
end

