--默认设置，如果pt中没有填对应的ID，那就返回默认的值
local l_defaultMinTime = 1  -- 默认最小时间
local l_defaultMaxTime = 2	-- 默认最大时间

--[[
1 普通喊话
2 时装
3 坐骑
4 朋友
5 配偶 pethosthp
]]--
pet_chat = {}
function pet_chat:GetChatType(pet_tid)
	if pt[pet_tid] == nil then -- 默认是普通喊话
		return 1
	else
		local n1 = 0
		local n2 = 0
		local n3 = 0
		local n4 = 0
		local n5 = 0
		
		if pt[pet_tid].petst ~= nil		then		n1 = #(pt[pet_tid].petst)	end
		if pt[pet_tid].petsz ~= nil		then		n2 = #(pt[pet_tid].petsz)	end
		if pt[pet_tid].petzq ~= nil		then		n3 = #(pt[pet_tid].petzq)	end
		if pt[pet_tid].petfr ~= nil		then		n4 = #(pt[pet_tid].petfr)	end
		if pt[pet_tid].petmr ~= nil		then		n5 = #(pt[pet_tid].petmr)	end
		
		local idx_pt_t = math.random(1,n1+n2+n3+n4+n5)
		LogPrint(idx_pt_t)
		if idx_pt_t <= n1 then				
			return 1
		elseif idx_pt_t <= n1+n2	then	
			return 2
		elseif idx_pt_t <= n1+n2+n3	then	
			return 3
		elseif idx_pt_t <= n1+n2+n3+n4 then
			return 4
		elseif idx_pt_t <= n1+n2+n3+n4+n5 then
			return 5
		end
	end
end

function pet_chat:GetNormalChat( pet_tid )
	if pt[pet_tid] == nil then
		return l_defaultString[1],math.random(l_defaultMinTime * 60000,l_defaultMaxTime * 60000)
	else
		local timemin = pt[pet_tid].mintime * 60000 --最小时间
		local timemax = pt[pet_tid].maxtime * 60000 --最大时间
		local pettbl = pt[pet_tid].petst
		local idx_pettbl = math.random(1,#pettbl)
		return petst[pettbl[idx_pettbl]],math.random(timemin,timemax)
	end
end
function pet_chat:GetBattleChat( pet_tid )
	if pt[pet_tid] == nil then
		return l_defaultString[2],math.random(l_defaultMinTime * 60000,l_defaultMaxTime * 60000)
