		local timemin = pt[pet_tid].mintime * 60000 --最小时间
		local timemax = pt[pet_tid].maxtime * 60000 --最大时间
		local pettbl = pt[pet_tid].petbt
		local idx_pettbl = math.random(1,#pettbl)
		return petbt[pettbl[idx_pettbl]],math.random(timemin,timemax)
	end
end
