function pet_chat:GetFashionChat( pet_tid, Fashion_tid )
	if pt[pet_tid] == nil then
		return l_defaultString[3],math.random(l_defaultMinTime * 60000,l_defaultMaxTime * 60000)
	else
		local timemin = pt[pet_tid].mintime * 60000 --最小时间
		local timemax = pt[pet_tid].maxtime * 60000 --最大时间
		if petsz[Fashion_tid] == nil then
			return l_defaultString[3],math.random(timemin,timemax)
		else
			return petsz[Fashion_tid],math.random(timemin,timemax)
		end
	end
end
