		return l_defaultString[4],math.random(l_defaultMinTime * 60000,l_defaultMaxTime * 60000)
	else
		local timemin = pt[pet_tid].mintime * 60000 --最小时间
		local timemax = pt[pet_tid].maxtime * 60000 --最大时间
		if petzq[Vehicle_tid] == nil then
			return l_defaultString[4],math.random(timemin,timemax)
		else
			return petzq[Vehicle_tid],math.random(timemin,timemax)
		end
	end
end

function pet_chat:GetGuardHPChat( pet_hp )
	for k,v in pairs(pethp) do
		if pet_hp*100 < k then
			return v,3600000
		end
	end
	return "",1000
end
function pet_chat:GetHostHPChat( host_hp )
	for k,v in pairs(ghosthp) do
		if host_hp*100 < k then
			return v,3600000
		end
	end
	return "",1000
end
